
public class day4월3일4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1. 구구단 전체를 출력하는 코드를 작성하세요.
		for(int i = 1; i<=9; i++){
			for(int j=1;j<=9;j++){
				System.out.printf("%d * %d = %d%n", i, j, i*j);
			}
		}
		
		
		
		int num =6;
		for(num=2; num<=9; num++){
			for(int i=1; i<=9; i++){
		
				System.out.println(num + "x" + i + "=" + num*i);
			}
			}
			
		
			
		
	}

}
