package korea;

public class congming {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   //방법2 2부터 10까지 2개씩 증가하면서 더한다
	 int i = 0;
	 int sum=0;
		for(i=2; i<=10; i+=2){
		sum +=i;
	}
		
	  System.out.println("방법2: 1부터 10가지의 짝수의합:"+ sum);
	
	
	}

}
